css:
.print-format {
    padding: 0.35in;
}

.row.section-break {
    margin: 30px 0 0;
}

.col-6 {
  display: inline-block;
  width: 49%;
  vertical-align: middle;
  word-break: break-all;
}

.col-3 {
  display: inline-block;
  width: 19%;
  vertical-align: middle;
  word-break: break-all;
}

.col-9 {
  display: inline-block;
  width: 79%;
  vertical-align: middle;
  word-break: break-all;
}

.col-7 {
  display: inline-block;
  width: 57%;
  vertical-align: middle;
  word-break: break-all;
}

.col-5 {
  display: inline-block;
  width: 40%;
  vertical-align: middle;
  word-break: break-all;
}

.text-right {
    text-align: right;
}

.text-center {
    text-align: center;
}

.table-sr {
    display: none;
}

#body {
    margin: 0 auto;
    background-color: #fff;
    font-family: 'Ubuntu', sans-serif;
    font-size: 13.5px;
}

header {
    margin-top: 30px;
}

header img {
    width: 30%;
}

header .text-right p {
    font-weight: 600;
    text-transform: capitalize;
    margin: 0;
    margin-top: 2px;
    line-height: 1.3;
    vertical-align: middle;
    font-size: 30px;
}

label {
    line-height: 1.3;
    font-weight: 700;
    margin-bottom: 5px;
}

.col-3 label {
    margin-bottom: 0;
}

div[data-fieldname="company_address_display"] label,
div[data-fieldname="address_display"] label {
    display: none;
}

div[data-fieldname="posting_date"] .col-xs-12:first-child,
div[data-fieldname="due_date"] .col-xs-12:first-child,
div[data-fieldname="place_of_supply"] .col-xs-12:first-child {
    width: 44%;
    display: inline-block;
    vertical-align: middle;
    padding: 0 15px;
}

div[data-fieldname="company_gstin"] .col-xs-12:first-child,
div[data-fieldname="customer_gstin"] .col-xs-12:first-child {
     width: 19%;
    display: inline-block;
    vertical-align: middle;
    padding: 0 15px;
}

div[data-fieldname="company_gstin"] .col-xs-12:first-child label,
div[data-fieldname="customer_gstin"] .col-xs-12:first-child label {
    margin-bottom: 0;
}

div[data-fieldname="posting_date"] .col-xs-12:last-child,
div[data-fieldname="due_date"] .col-xs-12:last-child,
div[data-fieldname="place_of_supply"] .col-xs-12:last-child {
    width: 54%;
    display: inline-block;
    vertical-align: middle;
    padding: 0 15px;

}

div[data-fieldname="company_gstin"] .col-xs-12:last-child,
div[data-fieldname="customer_gstin"] .col-xs-12:last-child {
    width: 79%;
    display: inline-block;
    vertical-align: middle;
    padding: 0 15px;
}

.important .value {
    font-size: inherit;
    font-weight: inherit;
}

div[data-fieldname="posting_date"] .col-xs-12:last-child,
div[data-fieldname="due_date"] .col-xs-12:last-child,
div[data-fieldname="place_of_supply"] .col-xs-12:last-child {
    text-align: right;
}

.table-bordered > thead > tr > th, .table-bordered > thead > tr > td {
    border: 1px solid #ddd!important;
    background-color: #e0f7fa!important;
    font-weight: 500 
}

.bank-details label {
    font-weight: normal;
}

.row.bank-details {
    padding-left: 15px;
}

div[data-fieldname="remarks"] div {
    width: 100%;
}

div[data-label="Additional Details"] .col-xs-6:last-child .row:nth-child(2) .col-xs-6:first-child {display: none}

div[data-label="Additional Details"] .col-xs-6:last-child .row:nth-child(2) .col-xs-6:last-child {
    width: 100%;
}

div[data-label="Additional Details"] .col-xs-6:last-child .col-xs-5,
div[data-label="Additional Details"] .col-xs-6:last-child .col-xs-7 {
    width: 50%;
    border: 1px solid #ddd;
    padding: 5px;
}

div[data-label="Additional Details"] .col-xs-6:last-child .col-xs-7 {
    padding-bottom: 8px;
    border-left: 0!important;
}

div[data-label="Additional Details"] .col-xs-6:last-child .col-xs-5 {
    text-align: right;
}

div[data-fieldname="in_words"] .col-xs-5,
div[data-fieldname="in_words"] .col-xs-7 {
    width: 100%!important;
}

div[data-fieldname="in_words"] .col-xs-5 {
    text-align: left!important;
    border-bottom: 0!important;
    padding: 5px 5px 0 5px!important;
}

div[data-fieldname="in_words"] .col-xs-7 {
    border-top: 0!important;
    padding: 0 0 5px 5px!important;
}

div[data-fieldname="base_grand_total"] .col-xs-7 {
    text-align: right;
}

div[data-fieldname="base_grand_total"] > div {
    background-color: #039be5;
    color: #fff;
} 

.data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.contact_details {
    border: 1px solid #ddd;
    padding: 8px;
}
